import React, { Component } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import Axios from 'axios';

import CalculatorSummary from '../CalculatorSummary/CalculatorSummary'

class CalculatorInputs extends Component {
    state = {
        errorMessage: '',
        dataSumbit: false,
        overHoursAmount: 0,
        overHoursGrossIncome: 0,
        overHoursNetIncome: 0,
        totalGrossIncome: 0,
        totalNetIncome: 0,
        workedMonthName: ''
    }
    render() {
        return (
            <Formik
                initialValues={{
                    workedMonth: 1,
                    workedHours: 250,
                    hourlyFee: 30
                }}
                validationSchema={Yup.object().shape({
                    workedHours: Yup.string()
                        .required('Worked hours is required'),
                    hourlyFee: Yup.string()
                        .required('Hourly fee is required'),
                })}
                onSubmit={fields => {
                    let intFields = {
                        ...fields
                    };

                    intFields.workedHours = parseInt(intFields.workedHours);
                    intFields.hourlyFee = parseInt(intFields.hourlyFee);

                    Axios.post('http://192.168.0.5:5000/salarycalculator', intFields)
                        .then(response => {
                            console.log(response.data)
                            this.setState({
                                dataSumbit: true,
                                workedMonthName: intFields.workedMonth,
                                workedHours: intFields.workedHours,
                                hourlyFee: intFields.hourlyFee,
                                overHoursAmount: response.data._summary.overHoursAmount,
                                overHoursGrossIncome: response.data._summary.overHoursGrossIncome,
                                overHoursNetIncome: response.data._summary.overHoursNetIncome,
                                totalGrossIncome: response.data._summary.totalGrossIncome,
                                totalNetIncome: response.data._summary.totalNetIncome
                            })
                        })
                        .catch(error => {
                            this.setState({ errorMessage: error.response.data.message })
                        })
                }}
                render={({ errors, status, touched }) => (
                    <Form>
                        <div className="form-group">
                            <label htmlFor="workedMonth">Worked Month: </label>
                            <Field as="select" name="workedMonth">
                                <option value="1">Styczeń</option>
                                <option value="2">Luty</option>
                                <option value="3">Marzec</option>
                                <option value="4">Kwiecień</option>
                                <option value="5">Maj</option>
                                <option value="6">Czerwiec</option>
                                <option value="7">Lipiec</option>
                                <option value="8">Sierpień</option>
                                <option value="9">Wrzesień</option>
                                <option value="10">Październik</option>
                                <option value="11">Listopad</option>
                                <option value="12">Grudzień</option>
                            </Field>
                        </div>
                        <div className="form-group">
                            <label htmlFor="workedHours">Worked Hours: </label>
                            <Field name="workedHours" type="text" className={'form-control' + (errors.workedHours && touched.workedHours ? ' is-invalid' : '')} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="hourlyFee">Hourly Fee: </label>
                            <Field name="hourlyFee" type="text" className={'form-control' + (errors.hourlyFee && touched.hourlyFee ? ' is-invalid' : '')} />
                        </div>
                        <div className="form-group">
                            <button type="submit" className="btn btn-primary mr-2">Calculate</button>
                            <button type="reset" className="btn btn-secondary">Reset</button>
                        </div>
                        <div>
                            <ErrorMessage name="workedMonth" component="div" className="invalid-feedback" />
                            <ErrorMessage name="workedHours" component="div" className="invalid-feedback" />
                            <ErrorMessage name="hourlyFee" component="div" className="invalid-feedback" />
                        </div>
                        <div>
                            {this.state.errorMessage}
                            <br></br>
                            <br></br>
                        </div>
                        <div>
                            {this.state.dataSumbit ? <CalculatorSummary
                                monthName={this.state.monthName}
                                workedHoursInMonth={this.state.workedHoursInMonth}
                                workedHours={this.state.workedHours}
                                hourlyFee={this.state.hourlyFee}
                                overHoursAmount={this.state.overHoursAmount}
                                overHoursGrossIncome={this.state.overHoursGrossIncome}
                                overHoursAmount={this.state.overHoursAmount}
                                overHoursNetIncome={this.state.overHoursNetIncome}
                                totalGrossIncome={this.state.totalGrossIncome}
                                totalNetIncome={this.state.totalNetIncome}
                            /> : ""}
                        </div>
                    </Form>
                )}
            />
        )
    }
}

export default CalculatorInputs;
