import React, { Component } from 'react';
import { Button, FormGroup, Label } from 'reactstrap';
import { Form, Field, Formik } from 'formik';
import './CalculatorForm.css';

import DynamicSelect from '../DynamicSelect/DynamicSelect';

class CalculatorForm extends Component {
    state = {
        workedMonth: 1
    }

    validateWorkedHoursInput(inputValue) {
        let error;
        if (!inputValue) {
            error = 'Worked hours is required';
        } else if (isNaN(inputValue)) {
            error = 'Invalid input format';
        } else if (inputValue < 0 || inputValue > 300) {
            error = 'Invalid worked hours'
        }
        return error;
    }

    validateHourlyFeeInput(inputValue) {
        let error;
        if (!inputValue) {
            error = 'Hourly fee is required';
        } else if (isNaN(inputValue)) {
            error = 'Invalid input format';
        } else if (inputValue < 1 || inputValue > 1000) {
            error = 'Invalid hourly fee'
        }
        return error;
    }

    handleSelectChange = (selectedValue) => {
        this.setState({
            workedMonth: parseInt(selectedValue) + 1
        });
    }

    render() {        
        return (
            <Formik
                initialValues={{
                    workedHoursInput: '',
                    hourlyFeeInput: '',
                }}

                onSubmit={formFields => {
                    let workedMonth = this.state.workedMonth;
                    formFields = {
                        ...formFields,
                        workedMonth
                    }
                    this.props.handleSubmitForm(formFields);
                }}

                onReset={() => {
                    this.props.handleReset();
                }}
            >
                {({ errors, touched }) => (
                    <Form className="form">

                    </Form>
                )}
            </Formik>
        );
    }
}

export default CalculatorForm;
