import React, { Component } from 'react';
import { Button, FormGroup, Label } from 'reactstrap';
import { Form, Field, ErrorMessage, Formik } from 'formik';
import * as Yup from 'yup';
import Axios from 'axios';
import './CalculatorInputs.css';

import DynamicSelect from '../DynamicSelect/DynamicSelect'
import CalculatorSummary from '../CalculatorSummary/CalculatorSummary'

class CalculatorInputs extends Component {
    state = {
        errorMessage: '',
        dataSumbit: false,
        workedMonth: 1,
        overHoursAmount: 0,
        overHoursGrossIncome: 0,
        overHoursNetIncome: 0,
        totalGrossIncome: 0,
        totalNetIncome: 0,
        monthName: [
            'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'
        ]
    }

    componentDidMount = () => {
        Axios.get('http://192.168.0.5:5000/SalaryCalculator')
            .then(response => {
                console.log(response.data)
            })
    }

    handleSelectChange = (selectedValue) => {
        this.setState({
            workedMonth: selectedValue
        });
    }

    render() {
        return (
            <Formik
                initialValues={{
                    workedHours: '',
                    hourlyFee: ''
                }}
                validationSchema={Yup.object().shape({
                    workedHours: Yup.string()
                        .required('Worked hours is required'),
                    hourlyFee: Yup.string()
                        .required('Hourly fee is required'),
                })}
                onSubmit={fields => {
                    let intFields = {
                        ...fields
                    };
                    intFields.workedHours = parseInt(intFields.workedHours);
                    intFields.hourlyFee = parseInt(intFields.hourlyFee);

                    Axios.post('http://192.168.0.5:5000/salarycalculator', intFields)
                        .then(response => {
                            console.log(response.data)
                            this.setState({
                                dataSumbit: true,
                                workedHours: intFields.workedHours,
                                hourlyFee: intFields.hourlyFee,
                                overHoursAmount: response.data.overHoursAmount,
                                overHoursGrossIncome: response.data.overHoursGrossIncome,
                                overHoursNetIncome: response.data.overHoursNetIncome,
                                totalGrossIncome: response.data.totalGrossIncome,
                                totalNetIncome: response.data.totalNetIncome
                            })
                        })
                        .catch(error => {
                            this.setState({ errorMessage: error.response.data.message })
                        })
                }}
                render={({ errors }) => (
                    <Form className="form">
                        <FormGroup>
                            <Label for="workedHours">Worked Hours:</Label>
                            <Field name="workedHours" type="name" className={'form-control' + (errors.workedMonth && errors.workedMonth ? ' is-invalid' : '')} />
                            <ErrorMessage name="workedHours" component="div" className="error-message" />
                        </FormGroup>
                        <FormGroup>
                            <Label for="workedHours">Month:</Label>
                            <DynamicSelect
                                arrayOfData={this.state.monthName}
                                onSelectChange={this.handleSelectChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="hourlyFee">Hourly Fee:</Label>
                            <Field name="hourlyFee" type="name" className={'form-control' + (errors.hourlyFee && errors.hourlyFee ? ' is-invalid' : '')} />
                            <ErrorMessage name="hourlyFee" component="div" className="error-message" />
                        </FormGroup>
                        <FormGroup>
                            <Button className="button" type="submit" color="secondary" className="button">Calculate!</Button>
                        </FormGroup>
                        <div>
                            {this.state.errorMessage}
                        </div>
                    </Form>
                )}
            />
        )
    }
}

export default CalculatorInputs;
