import React, { Component } from 'react';
import { Button, FormGroup, Label } from 'reactstrap';
import { Form, Field, ErrorMessage, Formik } from 'formik';
import * as Yup from 'yup';
import Axios from 'axios';
import './CalculatorInputs.css';

import DynamicSelect from '../DynamicSelect/DynamicSelect';
import CalculatorSummary from '../CalculatorSummary/CalculatorSummary';
import { isNullOrUndefined } from 'util';

class CalculatorInputs extends Component {
    state = {
        monthsWorkingHours: [],
        workingHoursInMonth: 0,
        errorMessage: '',
        isSummaryVisible: false,
        workedMonth: 1,
        overHoursAmount: 0,
        overHoursGrossIncome: 0,
        overHoursNetIncome: 0,
        totalGrossIncome: 0,
        totalNetIncome: 0,
        monthName: [
            'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'
        ]
    }

    componentDidMount = () => {
        Axios.get('http://192.168.0.5:5000/SalaryCalculator')
            .then(response => {
                this.setState({ monthsWorkingHours: response.data })
            })
    }

    handleSelectChange = (selectedValue) => {
        this.setState({
            workedMonth: parseInt(selectedValue) + 1
        });
    }

    handleCloseSummary = () => {
        this.setState({ isSummaryVisible: false });
    }

    validateWorkedHoursInput(inputValue) {
        let error;
        if (!inputValue) {
            error = 'Worked hours is required';
        }
        return error;
    }

    validateHourlyFeeInput(inputValue) {
        let error;
        if (!inputValue) {
            error = 'Hourly fee is required';
        }
        return error;
    }

    render() {
        return (
            <div>
                <h1>Signup</h1>
                <Formik
                    initialValues={{
                        workedHoursInput: '',
                        hourlyFeeInput: '',
                    }}
                    onSubmit={values => {
                        // same shape as initial values
                        console.log(values);
                    }}
                >
                    {({ errors, touched, isValidating }) => (
                        <Form className="form">
                            <FormGroup>
                                <Label for="workedHoursInput">Worked Hours:</Label>
                                <Field className="input" name="workedHoursInput" validate={this.validateWorkedHoursInput} />
                                {errors.workedHoursInput && touched.workedHoursInput && <div className="error-message">{errors.workedHoursInput}</div>}
                            </FormGroup>
                            <FormGroup>
                                <Label>Month:</Label>
                                <DynamicSelect
                                    monthName={this.state.monthName}
                                    monthsWorkingHours={this.state.monthsWorkingHours}
                                    onSelectChange={this.handleSelectChange} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="hourlyFeeInput">Hourly Fee:</Label>
                                <Field className="input" name="hourlyFeeInput" validate={this.validateHourlyFeeInput} />
                                {errors.hourlyFeeInput && touched.hourlyFeeInput && <div className="error-message">{errors.hourlyFeeInput}</div>}
                            </FormGroup>
                            <FormGroup>
                                <Button className="button" type="submit" color="secondary">Calculate!</Button>
                                <Button className="button" type="reset" color="secondary">Reset</Button>
                            </FormGroup>
                            <div className="error-message">
                                {this.state.errorMessage}
                            </div>
                        </Form>
                    )}
                </Formik>
            </div>
        )
    }
}

export default CalculatorInputs;
