import React, { Component } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';

class CalculatorInputs extends Component {
    render() {
        return (
            <Formik
                initialValues={{
                    workedMonth: '',
                    workedHours: '',
                    hourlyFee: ''
                }}
                validationSchema={Yup.object().shape({
                    workedMonth: Yup.string()
                        .required('Worked month is required'),
                        workedHours: Yup.string()
                        .required('Worked hours is required'),
                        hourlyFee: Yup.string()
                        .required('Hourly fee is required'),
                })}
                onSubmit={fields => {
                    alert('SUCCESS!! :-)\n\n' + JSON.stringify(fields, null, 4))
                }}
                render={({ errors, status, touched }) => (
                    <Form>
                        <div className="form-group">
                            <label htmlFor="workedMonth">Worked Month: </label>
                            <Field name="workedMonth" type="text" className={'form-control' + (errors.workedMonth && touched.workedMonth ? ' is-invalid' : '')} />
                            <ErrorMessage name="workedMonth" component="div" className="invalid-feedback" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="workedHours">Wroked Hours: </label>
                            <Field name="workedHours" type="text" className={'form-control' + (errors.workedHours && touched.workedHours ? ' is-invalid' : '')} />
                            <ErrorMessage name="workedHours" component="div" className="invalid-feedback" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="hourlyFee">Hourly Fee: </label>
                            <Field name="hourlyFee" type="text" className={'form-control' + (errors.hourlyFee && touched.hourlyFee ? ' is-invalid' : '')} />
                            <ErrorMessage name="hourlyFee" component="div" className="invalid-feedback" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <Field name="password" type="password" className={'form-control' + (errors.password && touched.password ? ' is-invalid' : '')} />
                            <ErrorMessage name="password" component="div" className="invalid-feedback" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="confirmPassword">Confirm Password</label>
                            <Field name="confirmPassword" type="password" className={'form-control' + (errors.confirmPassword && touched.confirmPassword ? ' is-invalid' : '')} />
                            <ErrorMessage name="confirmPassword" component="div" className="invalid-feedback" />
                        </div>
                        <div className="form-group">
                            <button type="submit" className="btn btn-primary mr-2">Register</button>
                            <button type="reset" className="btn btn-secondary">Reset</button>
                        </div>
                    </Form>
                )}
            />
        )
    }
}

export default CalculatorInputs;
